                ''' <summary>
                ''' Scan images asynchronously.
                ''' </summary>
                Private Sub startImageAcquistionButton_Click(ByVal sender As Object, ByVal e As EventArgs) Handles startImageAcquistionButton.Click
                    Try
                        ' get reference to the device
                        _device = _deviceManager.Devices.Find("KODAK Scanner: i5000")

                        ' subscribe to the device events
                        AddHandler _device.ImageAcquired, AddressOf _device_ImageAcquired
                        AddHandler _device.ScanCompleted, AddressOf _device_ScanCompleted
                        AddHandler _device.ScanCanceled, AddressOf _device_ScanCanceled
                        AddHandler _device.ScanFailed, AddressOf _device_ScanFailed

                        ' set Memory transfer mode
                        _device.TransferMode = TransferMode.Memory
                        ' disable scanner's UI
                        _device.ShowUI = False

                        ' open the device
                        _device.Open()
                        ' specify that 2 images must be acquired from scanner
                        _device.XferCount = 2

                        ' acquire images from device
                        _device.Acquire()
                    Catch ex As TwainException
                        MessageBox.Show(ex.Message)
                    End Try
                End Sub

                ''' <summary>
                ''' Image is acquired.
                ''' </summary>
                Private Sub _device_ImageAcquired(ByVal sender As Object, ByVal e As Vintasoft.Twain.ImageAcquiredEventArgs)
                    MessageBox.Show("Image is acquired.")

                    ' dispose the acquired image
                    e.Image.Dispose()

                    ' cancel the image acquistion
                    _device.CancelTransfer()
                End Sub

                ''' <summary>
                ''' Scan is completed.
                ''' </summary>
                Private Sub _device_ScanCompleted(ByVal sender As Object, ByVal e As EventArgs)
                    CloseDevice()

                    MessageBox.Show("Scan is completed.")
                End Sub

                ''' <summary>
                ''' Scan is canceled.
                ''' </summary>
                Private Sub _device_ScanCanceled(ByVal sender As Object, ByVal e As EventArgs)
                    CloseDevice()

                    MessageBox.Show("Scan is canceled.")
                End Sub

                ''' <summary>
                ''' Scan is failed.
                ''' </summary>
                Private Sub _device_ScanFailed(ByVal sender As Object, ByVal e As Vintasoft.Twain.ScanFailedEventArgs)
                    CloseDevice()

                    MessageBox.Show(String.Format("Scan is failed: {0}", e.ErrorString))
                End Sub

                ''' <summary>
                ''' Unsubscribe from device events and close the device.
                ''' </summary>
                Private Sub CloseDevice()
                    If Not _device Is Nothing Then
                        ' unsubscribe from device events
                        RemoveHandler _device.ImageAcquired, AddressOf _device_ImageAcquired
                        RemoveHandler _device.ScanCompleted, AddressOf _device_ScanCompleted
                        RemoveHandler _device.ScanCanceled, AddressOf _device_ScanCanceled
                        RemoveHandler _device.ScanFailed, AddressOf _device_ScanFailed

                        ' if device is not closed
                        If _device.State <> DeviceState.Closed Then
                            ' close the device
                            _device.Close()
                        End If
                    End If
                End Sub

