            {
                public partial class StopFeederDemoForm : Form
                {

                    #region Fields

                    /// <summary>
                    /// TWAIN device manager.
                    /// </summary>
                    DeviceManager _deviceManager;

                    /// <summary>
                    /// TWAIN device;
                    /// </summary>
                    Device _device;

                    /// <summary>
                    /// Determines that feeder must be stopped when first image is acquired.
                    /// </summary>
                    bool _stopFeederAterFirstAcquiredImage;

                    #endregion



                    #region Constructors

                    public StopFeederDemoForm()
                    {
                        InitializeComponent();

                        // create and open the device manager
                        _deviceManager = new DeviceManager(this);
                        _deviceManager.Open();
                    }

                    #endregion



                    #region Methods

                    /// <summary>
                    /// Start the image acquisition from scanner.
                    /// </summary>
                    private void startImageAcquisitionButton_Click(object sender, EventArgs e)
                    {
                        startImageAcquisitionButton.Enabled = false;

                        try
                        {
                            // get reference to the device
                            _device = _deviceManager.Devices.Find("KODAK Scanner: i5000");

                            // subscribe to the device events
                            _device.ImageAcquired += new EventHandler<ImageAcquiredEventArgs>(device_ImageAcquired);
                            _device.ScanCompleted += new EventHandler(device_ScanCompleted);
                            _device.ScanFailed += new EventHandler<ScanFailedEventArgs>(device_ScanFailed);

                            // set Memory transfer mode
                            _device.TransferMode = TransferMode.Memory;
                            // disable scanner's UI
                            _device.ShowUI = false;

                            // open the device
                            _device.Open();
                            // specify that 2 images must be acquired from scanner
                            _device.XferCount = 2;

                            // feeder must be stopped when first image is acquired 
                            _stopFeederAterFirstAcquiredImage = true;

                            // start asynchronous image acquisition
                            _device.Acquire();
                        }
                        catch (TwainException ex)
                        {
                            MessageBox.Show(ex.Message);

                            startImageAcquisitionButton.Enabled = true;
                        }
                    }

                    /// <summary>
                    /// Resume the image acquisition from scanner.
                    /// </summary>
                    private void resumeImageAcquisitionButton_Click(object sender, EventArgs e)
                    {
                        try
                        {
                            // resume asynchronous image acquisition
                            _device.Acquire();
                        }
                        catch (TwainException ex)
                        {
                            MessageBox.Show(ex.Message);

                            startImageAcquisitionButton.Enabled = true;
                        }
                    }

                    /// <summary>
                    /// Image is acquired.
                    /// </summary>
                    private void device_ImageAcquired(object sender, Vintasoft.Twain.ImageAcquiredEventArgs e)
                    {
                        MessageBox.Show("Image is acquired.");

                        // dispose the acquired image
                        e.Image.Dispose();

                        // if first image is acquired
                        if (_stopFeederAterFirstAcquiredImage)
                        {
                            _stopFeederAterFirstAcquiredImage = false;

                            try
                            {
                                // stop the feeder
                                _device.StopFeeder();

                                // feeder is stopped successfully
                                resumeImageAcquisitionButton.Enabled = true;

                                resumeImageAcquisitionButton.Enabled = true;
                            }
                            catch
                            {
                                // feeder is not stopped
                            }
                        }
                        else
                        {
                            startImageAcquisitionButton.Enabled = true;
                            resumeImageAcquisitionButton.Enabled = false;
                        }
                    }

