            /// <summary>
            /// Detects when paper is jammed.
            /// </summary>
            private void DetectPaperJam(Device device)
            {
                // disable device UI
                device.ShowUI = false;
                // disable device when images are acquired
                device.DisableAfterAcquire = true;

                // open the device
                device.Open();

                // set device settings
